import React, { useState } from 'react';

export default function App() {
  const [intent, setIntent] = useState('');
  const [style, setStyle] = useState('正式');
  const [language, setLanguage] = useState('中文');
  const [response, setResponse] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async () => {
    if (!intent.trim()) return;
    setLoading(true);
    setResponse('');

    const res = await fetch('http://<your-azure-vm-ip>:8000/write', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ intent, style, language })
    });

    const data = await res.json();
    setResponse(data.reply);
    setLoading(false);
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-br from-blue-100 via-white to-blue-200 p-6 font-sans">
      <div className="bg-white shadow-2xl rounded-2xl p-8 w-full max-w-2xl border border-gray-200">
        <h1 className="text-3xl font-extrabold text-blue-800 mb-6 text-center">📝 智能文案生成助手</h1>

        <textarea
          className="w-full p-4 border border-blue-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400 mb-6 text-gray-700 text-lg"
          rows={4}
          placeholder="请输入你要写的内容意图，例如：我要写一封会议邀请邮件"
          value={intent}
          onChange={(e) => setIntent(e.target.value)}
        ></textarea>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
          <div>
            <label className="block mb-1 text-sm font-semibold text-gray-600">选择文风</label>
            <select
              className="w-full border border-gray-300 rounded-lg p-2 focus:outline-none focus:ring-2 focus:ring-blue-300"
              value={style}
              onChange={(e) => setStyle(e.target.value)}
            >
              <option value="正式">正式</option>
              <option value="轻松">轻松</option>
              <option value="礼貌催促">礼貌催促</option>
              <option value="简洁干练">简洁干练</option>
            </select>
          </div>

          <div>
            <label className="block mb-1 text-sm font-semibold text-gray-600">选择语言</label>
            <select
              className="w-full border border-gray-300 rounded-lg p-2 focus:outline-none focus:ring-2 focus:ring-blue-300"
              value={language}
              onChange={(e) => setLanguage(e.target.value)}
            >
              <option value="中文">中文</option>
              <option value="英文">英文</option>
              <option value="丹麦语">丹麦语</option>
            </select>
          </div>
        </div>

        <button
          className="w-full bg-blue-600 hover:bg-blue-700 transition text-white font-semibold py-3 rounded-xl text-lg shadow-md"
          onClick={handleSubmit}
          disabled={loading}
        >
          {loading ? '生成中...' : '✨ 生成文案'}
        </button>

        {response && (
          <div className="mt-8">
            <h2 className="font-semibold text-xl text-gray-700 mb-3">🎯 AI 生成的文案：</h2>
            <div className="bg-gray-50 p-5 border border-gray-200 rounded-lg text-gray-800 leading-relaxed whitespace-pre-line">
              {response}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
