import React, { useState } from 'react';

export default function App() {
  const [intent, setIntent] = useState('');
  const [style, setStyle] = useState('正式');
  const [language, setLanguage] = useState('中文');
  const [response, setResponse] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async () => {
    if (!intent.trim()) return;
    setLoading(true);
    setResponse('');

    const res = await fetch('http://<your-azure-vm-ip>:8000/write', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ intent, style, language })
    });

    const data = await res.json();
    setResponse(data.reply);
    setLoading(false);
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100 p-6">
      <div className="bg-white shadow-lg rounded-2xl p-6 w-full max-w-xl">
        <h1 className="text-2xl font-bold mb-4">📝 智能文案生成助手</h1>
        <textarea
          className="w-full p-3 border rounded mb-4"
          rows={4}
          placeholder="请输入你要写的内容意图，例如：我要写一封会议邀请邮件"
          value={intent}
          onChange={(e) => setIntent(e.target.value)}
        ></textarea>

        <div className="flex items-center gap-3 mb-4">
          <label className="font-medium">选择风格：</label>
          <select
            className="border rounded p-2"
            value={style}
            onChange={(e) => setStyle(e.target.value)}
          >
            <option value="正式">正式</option>
            <option value="轻松">轻松</option>
            <option value="礼貌催促">礼貌催促</option>
            <option value="简洁干练">简洁干练</option>
          </select>
        </div>

        <div className="flex items-center gap-3 mb-4">
          <label className="font-medium">选择语言：</label>
          <select
            className="border rounded p-2"
            value={language}
            onChange={(e) => setLanguage(e.target.value)}
          >
            <option value="中文">中文</option>
            <option value="英文">英文</option>
            <option value="丹麦语">丹麦语</option>
          </select>
        </div>

        <button
          className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded"
          onClick={handleSubmit}
          disabled={loading}
        >
          {loading ? '生成中...' : '生成文案'}
        </button>

        {response && (
          <div className="mt-6">
            <h2 className="font-semibold mb-2">🎯 AI 生成的文案：</h2>
            <div className="bg-gray-50 p-4 border rounded whitespace-pre-line">
              {response}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
