import React, { useState } from "react";
import "./App.css";

export default function App() {
  const [intent, setIntent] = useState("");
  const [style, setStyle] = useState("Formal");
  const [language, setLanguage] = useState("English");
  const [response, setResponse] = useState("");
  const [lastResponse, setLastResponse] = useState("");
  const [loading, setLoading] = useState(false);
  const [copied, setCopied] = useState(false);
  const [history, setHistory] = useState([]);
  const [changes, setChanges] = useState([]);

  const BASE_URL =
    process.env.NODE_ENV === "development"
      ? "http://localhost:8000"
      : "https://chatbox-cyaxerbma4aucmh7.northeurope-01.azurewebsites.net";

  const handleSubmit = async () => {
    if (!intent.trim()) return;
    setLoading(true);
    setLastResponse(response);
    setResponse("");
    setChanges([]); 

    const res = await fetch(`${BASE_URL}/write`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ intent, style, language, history: history || [] }),
    });

    const data = await res.json();
    setResponse(data.reply);
    // Êõ¥Êñ∞ÂéÜÂè≤ËÆ∞ÂΩï
    setHistory([
      ...history,
      { role: "user", content: intent },
      { role: "assistant", content: data.reply },
    ]);
    setLoading(false);
  };

  const handleSubmitWithTemplate = async () => {
    if (!intent.trim()) return;
    setLoading(true);
    setLastResponse(response);
    setResponse("");
    setChanges([]); // Ê∏ÖÁ©∫‰∏ä‰∏ÄÊ¨°ÂèòÊõ¥ËÆ∞ÂΩï

    const res = await fetch(`${BASE_URL}/write_with_template`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ intent, style, language, history: history || [] }),
    });

    const data = await res.json();
    const parsed =
      typeof data.reply === "string" ? JSON.parse(data.reply) : data.reply;

    setResponse(parsed); // Ê≥®ÊÑèÔºöÁé∞Âú®ÊòØÂØπË±°Ôºå‰∏çÊòØÂ≠óÁ¨¶‰∏≤
    setHistory([
      ...history,
      { role: "user", content: intent },
      { role: "assistant", content: parsed.document }, // Âè™ËÆ∞ÂΩïÊ≠£Êñá
    ]);
    setLoading(false);
  };

  const handleUndo = () => {
  setResponse(lastResponse);

  const newHistory = [...history];
  for (let i = newHistory.length - 1; i >= 0; i--) {
    if (newHistory[i].role === "assistant") {
      newHistory[i] = { role: "assistant", content: lastResponse };
      break;
    }
  }
  setHistory(newHistory);
};

  const handleCopy = () => {
    if (response) {
      navigator.clipboard.writeText(response.document);
      setCopied(true);
      setTimeout(() => setCopied(false), 2000);
    }
  };

  return (
    <div className="page-container">
      <div className="chat-box">
        <h1 className="title">Hey Write!</h1>

        <textarea
          className="intent-textarea"
          rows={5}
          placeholder="Describe what you want to write, e.g., an invitation email for a meeting"
          value={intent}
          onChange={(e) => setIntent(e.target.value)}
        ></textarea>

        <div className="selectors">
          <div className="select-group">
            <label className="label">Choose Tone</label>
            <select
              className="select"
              value={style}
              onChange={(e) => setStyle(e.target.value)}
            >
              <option value="Formal">Formal</option>
              <option value="Casual">Casual</option>
              <option value="Polite Push">Polite Push</option>
              <option value="Concise & Direct">Concise & Direct</option>
              <option value="Humorous">Humorous</option>
              <option value="Creative">Creative</option>
            </select>
          </div>

          <div className="select-group">
            <label className="label">Language</label>
            <select
              className="select"
              value={language}
              onChange={(e) => setLanguage(e.target.value)}
            >
              <option value="English">English</option>
              <option value="Danish">Dansk</option>
              <option value="Chinese">‰∏≠Êñá</option>
            </select>
          </div>
        </div>

        <div className="response-controls">
          {lastResponse && (
            <button
              className="undo-button"
              onClick={handleUndo}
            >
              ‚¨ÖÔ∏è Back to Last Version
            </button>
          )}

          <button className="reset-button" onClick={() => setHistory([])}>
            üîÑ Reset Conversation
          </button>
        </div>

        <div className="generate-buttons">
          <button
            className="generate-button"
            onClick={handleSubmitWithTemplate}
            disabled={loading}
          >
            Generate with Template
          </button>
          <button
            className="generate-button"
            onClick={handleSubmit}
            disabled={loading}
          >
            {loading
              ? "Writing... It takes around 1 minute"
              : "‚ú® Generate something wild"}
          </button>
        </div>

        {response && (
          <div className="response-box">
            <h2 className="response-title">Your Draft</h2>
            <button className="copy-button" onClick={handleCopy}>
              {copied ? "Copied!" : "Copy"}
            </button>
            <div className="response-text">
              <pre style={{ whiteSpace: "pre-wrap" }}>{response.document}</pre>
            </div>

            {response.changes && response.changes.length > 0 && (
              <div className="changes-box">
                <h3 className="changes-title">Changes Made</h3>
                <ul>
                  {response.changes.map((change, idx) => (
                    <li key={idx}>{change}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        )}

        {changes.length > 0 && (
          <div className="changes-box">
            <h3 className="changes-title">üìù Changes Made</h3>
            <ul className="changes-list">
              {changes.map((change, idx) => (
                <li key={idx} className="change-item">
                  {change}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>

      <div className="history-panel">
        <h2>üïí Conversation History</h2>
        {history.map((msg, index) => (
          <div key={index} className={`msg ${msg.role}`}>
            <strong>{msg.role === "user" ? "You" : "HeyWrite"}:</strong>
            <p>{msg.content}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
