import React, { useState } from 'react';
import './App.css';

export default function App() {
  const [intent, setIntent] = useState('');
  const [style, setStyle] = useState('正式');
  const [language, setLanguage] = useState('中文');
  const [response, setResponse] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async () => {
    if (!intent.trim()) return;
    setLoading(true);
    setResponse('');

    const res = await fetch('http://<your-azure-vm-ip>:8000/write', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ intent, style, language })
    });

    const data = await res.json();
    setResponse(data.reply);
    setLoading(false);
  };

  return (
    <div className="page-container">
      <div className="chat-box">
        <h1 className="title">📝 智能文案生成助手</h1>

        <textarea
          className="intent-input"
          rows={4}
          placeholder="请输入你要写的内容意图，例如：我要写一封会议邀请邮件"
          value={intent}
          onChange={(e) => setIntent(e.target.value)}
        ></textarea>

        <div className="selectors">
          <div className="select-group">
            <label className="label">选择文风</label>
            <select
              className="select"
              value={style}
              onChange={(e) => setStyle(e.target.value)}
            >
              <option value="正式">正式</option>
              <option value="轻松">轻松</option>
              <option value="礼貌催促">礼貌催促</option>
              <option value="简洁干练">简洁干练</option>
            </select>
          </div>

          <div className="select-group">
            <label className="label">选择语言</label>
            <select
              className="select"
              value={language}
              onChange={(e) => setLanguage(e.target.value)}
            >
              <option value="中文">中文</option>
              <option value="英文">英文</option>
              <option value="丹麦语">丹麦语</option>
            </select>
          </div>
        </div>

        <button
          className="generate-button"
          onClick={handleSubmit}
          disabled={loading}
        >
          {loading ? '生成中...' : '✨ 生成文案'}
        </button>

        {response && (
          <div className="response-box">
            <h2 className="response-title">🎯 AI 生成的文案：</h2>
            <div className="response-text">
              {response}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
